function detect_window() {
	
	// workaround for mobile svg support
	if ($(window).width() <= 550) {
		$('#logo-img').attr('src', 'img-min/logo.png');
		$('.arrow-down').attr('href', '#row-0');
	} else {
		$('#logo-img').attr('src', 'img-min/logo.svg');
		$('.arrow-down').attr('href', '#row-1');
	}
}

function init() {
	animate_revealer();
	animate_text();
	detect_window();
}

function animate_revealer() {
		var rev_logo = new RevealFx(document.querySelector('#rev-logo'), {
			revealSettings : {
				bgcolor: blue,
				direction: 'lr',
				delay: 1500,
				duration: 250,
				easing: 'easeInOutQuint',
				onCover: function(contentEl, revealerEl) {
					contentEl.style.opacity = 1;
				}
			}
		});
		
	rev_logo.reveal();
	
	var rev_featured = new RevealFx(document.querySelector('#rev-featured'), {
			revealSettings : {
				bgcolor: featured,
				direction: 'bt',
				delay: 500,
				duration: 500,
				easing: 'easeInOutQuint',
				onCover: function(contentEl, revealerEl) {
					contentEl.style.opacity = 1;
				}
			}
		});
	
	$('.featured-img').attr('src', 'img-min/project-cover/featured-0.png');
	rev_featured.reveal();
}

function animate_text() {
	var textTimeline = anime.timeline({
	  //direction: 'alternate',
	  loop: false,
	});
	
	textTimeline
		.add({
			targets: '.title-1',
			rotate: [1.5, 0],
			opacity: 1,
			duration: 500,
			delay: 1000,
			easing: 'easeInOutSine',
		})
		.add({
			targets: '.title-2',
			rotate: [1.5, 0],
			opacity: 1,
			duration: 500,
			offset: 1100,
			easing: 'easeInOutSine',
		})
		.add({
			targets: '.title-3',
			rotate: [1.5, 0],
			opacity: 1,
			duration: 500,
			offset: 1200,
			easing: 'easeInOutSine',
		})
		.add({
			targets: '.arrow-link',
			opacity: 1,
			duration: 200,
			offset: 1500,
			easing: 'easeInOutSine',
		})
}
